import React, { useState } from 'react';
import axios from 'axios';

const CoverLetterGenerator = () => {
  const [summary, setSummary] = useState('Highly adaptable individual with strong communication skills and a passion for working in fast-paced environments. My background has honed my ability to work well in a team, and my collaborative mindset will be a valuable asset in a fullstack engineering role. Alongside my technical skills in Node.js, Express.js, SQL, MongoDB, Websocket, Docker, AWS... I am committed to delivering efficient and effective solutions to my team and clients.');
  const [skills, setSkills] = useState('Back-End Node.js / Express.js Linux MVC TDD MySQL, MongoDB HTTP, HTTPS, WebSocket BeatMatcher Full Stack');
  const [experience, setExperience] = useState('');
  const [education, setEducation] = useState('National Taiwan Normal University Bachelor of Physics');
  const [jobDescription, setJobDescription] = useState('');
  const [companyProducts, setCompanyProducts] = useState('');
  const [generatedCoverLetter, setGeneratedCoverLetter] = useState('');
  const [presets, setPresets] = useState([]);
  const [selectedPreset, setSelectedPreset] = useState(null);

  const savePreset = () => {
    const newPreset = {
      summary,
      skills,
      experience,
      education,
      jobDescription,
      companyProducts
    };
    setPresets([...presets, newPreset]);
  };

  const loadPreset = (preset) => {
    setSummary(preset.summary);
    setSkills(preset.skills);
    setExperience(preset.experience);
    setEducation(preset.education);
    setJobDescription(preset.jobDescription);
    setCompanyProducts(preset.companyProducts);
  };

  const generateCoverLetter = async () => {
    try {
      const response = await axios.post('http://localhost:3001/generate-cover-letter', {
        summary,
        skills,
        experience,
        education,
        jobDescription,
        companyProducts
      });
      const coverLetter = response.data.coverLetter.content;
      console.log(coverLetter);
      setGeneratedCoverLetter(coverLetter);
    } catch (error) {
      console.error('Error generating cover letter:', error);
    }
  };

  return (
    <div>
      <div>
        {/* 輸入摘要、技能、經驗、教育等信息 */}
        {/* 範例： */}
        <label>Summary</label>
        <textarea value={summary} onChange={(e) => setSummary(e.target.value)} placeholder="Summary" />
        <label>Skills</label>
        <textarea value={skills} onChange={(e) => setSkills(e.target.value)} placeholder="Skills" />
        <label>Experience</label>
        <textarea value={experience} onChange={(e) => setExperience(e.target.value)} placeholder="Experience" />
        <label>Education</label>
        <textarea value={education} onChange={(e) => setEducation(e.target.value)} placeholder="Education" />
      </div>
      <div>
        <label>Job Description</label>
        <textarea value={jobDescription} onChange={(e) => setJobDescription(e.target.value)} placeholder="Job Description" />
        <label>Company Products</label>
        <textarea value={companyProducts} onChange={(e) => setCompanyProducts(e.target.value)} placeholder="Company Products" />
      </div>
      <button onClick={generateCoverLetter}>Generate Cover Letter</button>
      <button onClick={savePreset}>Save Preset</button>
      <select onChange={(e) => loadPreset(JSON.parse(e.target.value))}>
        <option value="">Select Preset</option>
        {presets.map((preset, index) => (
          <option key={index} value={JSON.stringify(preset)}>Preset {index + 1}</option>
        ))}
      </select>
      <div>
        {/* Display the generated cover letter */}
        {generatedCoverLetter && (
          <div>
            <h2>Generated Cover Letter:</h2>
            <p>{generatedCoverLetter}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default CoverLetterGenerator;
